import React, { useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import './Dashboard.css';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Dashboard({ token, onLogout }) {
  const [results, setResults] = useState(null);
  const [error, setError] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [dragActive, setDragActive] = useState(false);

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = async (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const file = e.dataTransfer.files[0];
    if (file) {
      await handleFileUpload(file);
    }
  };

  const handleFileUpload = async (file) => {
    setIsUploading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:8000/analyze', formData, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setResults(response.data);
      setError('');
    } catch (err) {
      setError('Error analyzing file');
      setResults(null);
    }
    setIsUploading(false);
  };

  const chartData = results ? {
    labels: ['Positive', 'Neutral', 'Negative'],
    datasets: [{
      label: 'Sentiment Distribution',
      data: [
        results.statistics.positive,
        results.statistics.neutral,
        results.statistics.negative
      ],
      backgroundColor: [
        'rgba(75, 192, 192, 0.8)',
        'rgba(255, 206, 86, 0.8)',
        'rgba(255, 99, 132, 0.8)',
      ],
      borderColor: [
        'rgba(75, 192, 192, 1)',
        'rgba(255, 206, 86, 1)',
        'rgba(255, 99, 132, 1)',
      ],
      borderWidth: 1,
    }],
  } : null;

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { position: 'top' },
      title: { display: true, text: 'Sentiment Analysis Results' }
    }
  };

  return (
    <div className="dashboard-wrapper">
      <nav className="dashboard-nav">
        <h1>Sentiment Analysis Dashboard</h1>
        <button onClick={onLogout} className="logout-button">
          Logout
          <span className="button-effect"></span>
        </button>
      </nav>

      <div className="dashboard-content">
        <div className="upload-section"
             onDragEnter={handleDrag}
             onDragLeave={handleDrag}
             onDragOver={handleDrag}
             onDrop={handleDrop}>

          <input
            type="file"
            accept=".csv"
            onChange={(e) => handleFileUpload(e.target.files[0])}
            className="file-input"
            id="file-input"
          />

          <label htmlFor="file-input" className={`drop-zone ${dragActive ? 'drag-active' : ''}`}>
            <div className="upload-icon"></div>
            <p>Drag & Drop your CSV file here or click to browse</p>
            {isUploading && <div className="loader"></div>}
          </label>
        </div>

        {error && <div className="error-message">{error}</div>}

        {results && (
          <div className="results-container">
            <div className="chart-section">
              <Bar data={chartData} options={chartOptions} />
            </div>

            <div className="table-section">
              <h3>Detailed Results</h3>
              <div className="table-wrapper">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Text</th>
                      <th>Sentiment</th>
                      <th>Timestamp</th>
                    </tr>
                  </thead>
                  <tbody>
                    {results.results.map((result) => (
                      <tr key={result.id} className={`sentiment-${result.sentiment}`}>
                        <td>{result.id}</td>
                        <td>{result.text}</td>
                        <td>
                          <span className={`sentiment-badge ${result.sentiment}`}>
                            {result.sentiment}
                          </span>
                        </td>
                        <td>{result.timestamp}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
