import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Line, Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, LineElement, PointElement, ArcElement, Title, Tooltip, Legend } from 'chart.js';
import './Dashboard.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale, 
  LinearScale, 
  BarElement, 
  LineElement, 
  PointElement, 
  ArcElement,
  Title, 
  Tooltip, 
  Legend
);

function Dashboard({ token, onLogout }) {
  const [results, setResults] = useState(null);
  const [error, setError] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [scrollProgress, setScrollProgress] = useState(0);
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [timeBasedData, setTimeBasedData] = useState(null);
  const [selectedTimeframe, setSelectedTimeframe] = useState('hourly');
  const [selectedMetric, setSelectedMetric] = useState('sentiment');

  useEffect(() => {
    const observerOptions = {
      threshold: 0.1,
      rootMargin: '0px 0px -50px 0px'
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate-in');
          observer.unobserve(entry.target);
        }
      });
    }, observerOptions);

    const animatedElements = document.querySelectorAll('.scroll-animate');
    animatedElements.forEach(el => observer.observe(el));

    return () => observer.disconnect();
  }, [results]);

  useEffect(() => {
    const handleScroll = () => {
      // Calculate scroll progress
      const totalScroll = document.documentElement.scrollHeight - window.innerHeight;
      const currentScroll = window.pageYOffset;
      const progress = (currentScroll / totalScroll) * 100;
      setScrollProgress(progress);

      // Show/hide scroll to top button
      setShowScrollTop(currentScroll > 300);

      // Handle scroll animations
      const scrollElements = document.querySelectorAll('.scroll-animate');
      scrollElements.forEach((element) => {
        const elementTop = element.getBoundingClientRect().top;
        const elementBottom = element.getBoundingClientRect().bottom;
        const isVisible = (elementTop < window.innerHeight - 100) && (elementBottom > 0);

        if (isVisible) {
          element.classList.add('animate-in');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = async (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const file = e.dataTransfer.files[0];
    if (file) {
      await handleFileUpload(file);
    }
  };

  const handleFileUpload = async (file) => {
    setIsUploading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:8000/analyze', formData, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setResults(response.data);
      setError('');
    } catch (err) {
      setError('Error analyzing file');
      setResults(null);
    }
    setIsUploading(false);
  };

  const processTimeBasedData = (data) => {
    if (!data) return null;

    const timeGroups = data.reduce((acc, item) => {
      const date = new Date(item.timestamp);
      const hour = date.getHours();
      const key = `${hour}:00`;

      if (!acc[key]) {
        acc[key] = { positive: 0, negative: 0, neutral: 0 };
      }
      acc[key][item.sentiment]++;
      return acc;
    }, {});

    return {
      labels: Object.keys(timeGroups),
      datasets: [
        {
          label: 'Positive',
          data: Object.values(timeGroups).map(g => g.positive),
          borderColor: 'rgba(34, 197, 94, 1)',
          backgroundColor: 'rgba(34, 197, 94, 0.2)',
        },
        {
          label: 'Negative',
          data: Object.values(timeGroups).map(g => g.negative),
          borderColor: 'rgba(239, 68, 68, 1)',
          backgroundColor: 'rgba(239, 68, 68, 0.2)',
        },
        {
          label: 'Neutral',
          data: Object.values(timeGroups).map(g => g.neutral),
          borderColor: 'rgba(255, 159, 0, 1)',
          backgroundColor: 'rgba(255, 159, 0, 0.2)',
        }
      ]
    };
  };

  const MetricCard = ({ title, value, trend, icon }) => (
    <div className="metric-card scroll-animate fade-up">
      <div className="metric-icon">{icon}</div>
      <div className="metric-content">
        <h3>{title}</h3>
        <div className="metric-value">{value}</div>
        {trend && (
          <div className={`metric-trend ${trend > 0 ? 'positive' : 'negative'}`}>
            {trend > 0 ? 'â†‘' : 'â†“'} {Math.abs(trend)}%
          </div>
        )}
      </div>
    </div>
  );

  const chartData = results ? {
    labels: ['Positive', 'Neutral', 'Negative'],
    datasets: [{
      label: 'Sentiment Distribution',
      data: [
        results.statistics.positive,
        results.statistics.neutral,
        results.statistics.negative
      ],
      backgroundColor: [
        'rgba(34, 197, 94, 0.8)',  // Green for positive
        'rgba(255, 159, 0, 0.8)',  // Orange for neutral
        'rgba(239, 68, 68, 0.8)',  // Red for negative
      ],
      borderColor: [
        'rgb(34, 197, 94)',  // Solid green
        'rgb(255, 159, 0)',  // Solid orange
        'rgb(239, 68, 68)',  // Solid red
      ],
      borderWidth: 1,
    }],
  } : null;

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { position: 'top' },
      title: { display: true, text: 'Sentiment Analysis Results' }
    }
  };

  return (
    <div className="dashboard-wrapper">
      {/* Add scroll progress indicator */}
      <div 
        className="scroll-progress" 
        style={{ transform: `scaleX(${scrollProgress / 100})` }}
      />

      {/* Add scroll to top button */}
      <div 
        className={`scroll-top ${showScrollTop ? 'visible' : ''}`}
        onClick={scrollToTop}
      >
        â†‘
      </div>

      <nav className="dashboard-nav">
        <h1>Sentiment Analysis Dashboard</h1>
        <button onClick={onLogout} className="logout-button">
          Logout
          <span className="button-effect"></span>
        </button>
      </nav>

      <div className="dashboard-content">
        <div className="upload-section scroll-animate fade-up"
             onDragEnter={handleDrag}
             onDragLeave={handleDrag}
             onDragOver={handleDrag}
             onDrop={handleDrop}>

          <input
            type="file"
            accept=".csv"
            onChange={(e) => handleFileUpload(e.target.files[0])}
            className="file-input"
            id="file-input"
          />

          <label htmlFor="file-input" className={`drop-zone ${dragActive ? 'drag-active' : ''}`}>
            <div className="upload-icon"></div>
            <p>Drag & Drop your CSV file here or click to browse</p>
            {isUploading && <div className="loader"></div>}
          </label>
        </div>

        {error && <div className="error-message scroll-animate fade-up">{error}</div>}

        {results && (
          <>
            {/* Metrics Overview */}
            <div className="metrics-grid">
              <MetricCard
                title="Total Reviews"
                value={results.results.length}
                icon="ðŸ“Š"
              />
              <MetricCard
                title="Positive Sentiment"
                value={`${((results.statistics.positive / results.results.length) * 100).toFixed(1)}%`}
                trend={5.2}
                icon="ðŸ˜Š"
              />
              <MetricCard
                title="Neutral Sentiment"
                value={`${((results.statistics.neutral / results.results.length) * 100).toFixed(1)}%`}
                trend={0.5}
                icon="ðŸ˜"
              />
              <MetricCard
                title="Negative Sentiment"
                value={`${((results.statistics.negative / results.results.length) * 100).toFixed(1)}%`}
                trend={-2.1}
                icon="ðŸ˜”"
              />
            </div>

            {/* Main Charts Section */}
            <div className="charts-grid">
              {/* Sentiment Distribution */}
              <div className="chart-section scroll-animate fade-up">
                <h3>Sentiment Distribution</h3>
                <Doughnut 
                  data={{
                    labels: ['Positive', 'Neutral', 'Negative'],
                    datasets: [{
                      data: [
                        results.statistics.positive,
                        results.statistics.neutral,
                        results.statistics.negative
                      ],
                      backgroundColor: [
                        'rgba(34, 197, 94, 0.8)',
                        'rgba(255, 159, 0, 0.8)',
                        'rgba(239, 68, 68, 0.8)'
                      ]
                    }]
                  }}
                  options={{
                    responsive: true,
                    plugins: {
                      legend: { position: 'bottom' }
                    }
                  }}
                />
              </div>

              {/* Time-based Analysis */}
              <div className="chart-section scroll-animate fade-up">
                <h3>Sentiment Trends Over Time</h3>
                <div className="chart-controls">
                  <select 
                    value={selectedTimeframe}
                    onChange={(e) => setSelectedTimeframe(e.target.value)}
                  >
                    <option value="hourly">Hourly</option>
                    <option value="daily">Daily</option>
                    <option value="weekly">Weekly</option>
                  </select>
                </div>
                <Line 
                  data={processTimeBasedData(results.results)}
                  options={{
                    responsive: true,
                    plugins: {
                      legend: { position: 'bottom' }
                    }
                  }}
                />
              </div>
            </div>

            <div className="results-container">
              <div className="chart-section scroll-animate fade-up">
                <Bar data={chartData} options={chartOptions} />
              </div>

              <div className="table-section scroll-animate fade-up">
                <h3>Detailed Results</h3>
                <div className="table-wrapper">
                  <table>
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>Text</th>
                        <th>Sentiment</th>
                        <th>Timestamp</th>
                      </tr>
                    </thead>
                    <tbody>
                      {results.results.map((result, index) => (
                        <tr 
                          key={result.id} 
                          className={`sentiment-${result.sentiment} scroll-animate fade-up`}
                          style={{ animationDelay: `${index * 0.1}s` }}
                        >
                          <td>{result.id}</td>
                          <td>{result.text}</td>
                          <td>
                            <span className={`sentiment-badge ${result.sentiment}`}>
                              {result.sentiment}
                            </span>
                          </td>
                          <td>{result.timestamp}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
